-- Create "transfers" table
CREATE TABLE "transfers" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "from_pubkey" character varying NOT NULL, "to_pubkey" character varying NOT NULL, "name" character varying NOT NULL, "sender_balance" character varying NOT NULL, "amount" character varying NOT NULL, "memo_id" bigint NOT NULL, "created_at" timestamptz NOT NULL, "status" character varying NOT NULL, "deploy" character varying NOT NULL, "user_transfers" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "transfers_users_transfers" FOREIGN KEY ("user_transfers") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
